using Microsoft.ManagementConsole;\nusing System;\n\nnamespace CustomSnapIn\n{\n    [ActionPane(Verbs = new string[] { ActionPane.RefreshVerb })]\n    public class MyRootScopeNode : ScopeNode\n    {\n        protected override void OnInitialize()\n        {\n            base.OnInitialize();\n            Name = "My Snap-in Root";\n            DisplayName = "My Snap-in Display Name";\n\n            // Add child nodes here if needed\n            this.AddChild(new MyFirstChild());\n        }\n    }\n\n    public class MySnapIn : SnapIn\n    {\n        protected override void OnInitialize(InitializationEvent e)\n        {\n            base.OnInitialize(e);\n            this.RootNode = new MyRootScopeNode();\n        }\n    }\n}