// Example of passing necessary data instead of entire session\npublic class UserController\n{\n    private readonly IBusinessLayer _businessLayer;\n\n    public UserController(IBusinessLayer businessLayer)\n    {\n        _businessLayer = businessLayer;\n    }\n\n    public void HandleRequest()\n    {\n        // Assume UserSession contains user-specific information needed by the business logic\n        var userId = (int)HttpContext.Current.Session["UserId"];\n        var userName = HttpContext.Current.Session["UserName"] as string;\n\n        // Create a DTO with necessary data\n        var userData = new UserDataDto\n        {\n            UserId = userId,\n            UserName = userName\n        };\n\n        // Pass the DTO to the Business layer\n        _businessLayer.ProcessUserData(userData);\n    }\n}\n\npublic class UserDataDto\n{\n    public int UserId { get; set; }\n    public string UserName { get; set; }\n}\n\npublic interface IBusinessLayer\n{\n    void ProcessUserData(UserDataDto userData);\n}