using System;\nusing TestStack.White;\nusing TestStack.White.UIItems.WindowItems;\n\nnamespace MessageBoxTest\n{\n    class Program\n    {\n        [STAThread]\n        static void Main()\n        {\n            // Start the application\n            var application = Application.Launch("path_to_your_application.exe");\n            \n            // Get the main window of the application\n            Window mainWindow = application.GetWindow("MainWindowTitle", InitializeOption.NoCache);\n\n            // Trigger the action that shows the message box (e.g., click a button)\n            mainWindow.Get<Button>("YourButtonId").Click();\n\n            // Wait for the message box to appear\n            var msgBox =\n                application.MessageBoxWaiter.WaitUntilFound(TimeSpan.FromSeconds(10));\n\n            // Check the text of the message box\n            if (msgBox.Content.ToString().Equals("Howdy"))\n            {\n                Console.WriteLine("Message Box content verified successfully.");\n            }\n            else\n            {\n                Console.WriteLine("Message Box content verification failed.");\n            }\n\n            // Optionally, click OK or Cancel on the message box\n            msgBox.Get<Button>("OK").Click();\n\n            application.Kill();\n        }\n    }\n}