using System;\nusing System.Xml;\n\nclass Program\n{\n    static void Main()\n    {\n        var xmlDoc = new XmlDocument();\n        xmlDoc.Load("yourfile.xml"); // Load your XML file\n\n        // Create a namespace manager and add the necessary namespace.\n        XmlNamespaceManager nsmgr = new XmlNamespaceManager(xmlDoc.NameTable);\n        nsmgr.AddNamespace("ink", "http://www.inktomi.com/");\n\n        // Use the namespace in the XPath query\n        XmlNodeList nodes = xmlDoc.SelectNodes("//results/ink:result", nsmgr);\n\n        if (nodes != null)\n        {\n            foreach (XmlNode node in nodes)\n            {\n                Console.WriteLine(node.OuterXml);\n            }\n        }\n    }\n}