using System;\nusing System.Collections.Generic;\nusing Microsoft.VisualStudio.TextTemplating;\n\npublic static class HtmlGenerator\n{\n    private static readonly Engine engine = new Engine();\n\n    public static string GenerateHtml(string templateContent, object dataModel)\n    {\n        return engine.ProcessTemplate(templateContent, CreateHost(dataModel));\n    }\n\n    private static ITextTemplatingEngineHost CreateHost(object model)\n    {\n        var host = new Engine();\n        var session = new Dictionary<string, object>();\n        session["model"] = model;\n        host.Session = session;\n        return host;\n    }\n}