using System;\nusing Fiddler;\n\npublic class Program\n{\n    static void Main(string[] args)\n    {\n        // Initialize and start capturing traffic\n        FiddlerApplication.Startup(8877, true, false);\n\n        // Set up a delegate for handling each request\n        FiddlerApplication.BeforeRequest += Session_BeforeRequest;\n        \n        // Keep the program running\n        Console.WriteLine("Press any key to end...");\n        Console.ReadKey();\n\n        // Clean up\n        FiddlerApplication.Shutdown();\n    }\n\n    static void Session_BeforeRequest(Session oSession)\n    {\n        if (oSession.HostnameIs("sharepoint.thecompany.net"))\n        {\n            Console.WriteLine($"Requested URL: {oSession.url}");\n        }\n    }\n}