private ISessionFactory CreateSessionFactory()\n  {\n      return Fluently.Configure()\n          .Database(MsSqlConfiguration.MsSql2005\n              .ConnectionString(c => c.Is(@"Data Source=foo;Initial Catalog=bar;Integrated Security=True"))\n              .ProxyFactoryFactory<CastleProxyFactoryFactory>()) // Set Bytecode Provider\n          .Mappings(m => m.AutoMappings.Add(AutoPersistenceModel.MapEntitiesFromAssemblyOf<Employee>()\n              .Where(t => t.Namespace.Contains("Entities")))) // You can use your namespace filtering here again, ensure it's correct\n          .ExposeConfiguration(BuildSchema)\n          .BuildSessionFactory();\n  }