using System;\nusing System.Text;\n\npublic class Program\n{\n    public static void Main()\n    {\n        string inputString = "Your String Here";\n        \n        // Convert the string to a byte array using UTF-8 encoding\n        byte[] octetString = Encoding.UTF8.GetBytes(inputString);\n        \n        // The 'octetString' can now be used as an OctetString in LDAP operations\n        Console.WriteLine(BitConverter.ToString(octetString));\n    }\n}