// DTO Class mirroring Rec class with explicit nullable fields\npublic class SomeTableDto {\n    public string Field1 { get; set; }\n    public bool? IsField1Null => string.IsNullOrEmpty(Field1);\n    public int? Field2 { get; set; }\n    public bool? IsField2Null => !Field2.HasValue;\n}\n\n// Web service method example\n[WebMethod]\npublic SomeTableDto GetSomeTableRecord(int id) {\n    // Fetch and convert Rec to DTO \n    var rec = _dal.GetSomeTableRecById(id);\n    var dto = new SomeTableDto() {\n        Field1 = rec.Field1,\n        Field2 = rec.Field2Isnull ? (int?)null : rec.Field2\n    };\n    return dto;\n}