using System;\nusing Microsoft.BizTalk.ExplorerOM;\n\npublic class BizTalkHostProcessFinder\n{\n    public static void GetHostProcessInfo()\n    {\n        BtsCatalogExplorer catalog = new BtsCatalogExplorer();\n        string connectionString = "Server=.;Database=BizTalkMgmtDB;Integrated Security=True;";\n        catalog.ConnectionString = connectionString;\n        \n        foreach (HostInstance hostInstance in catalog.HostInstances)\n        {\n            Console.WriteLine($"Host Name: {hostInstance.Name}, Host Type: {hostInstance.HostType}, Process ID: {hostInstance.ProcessId}");\n        }\n    }\n}