using System;\nusing System.Runtime.InteropServices;\n\npublic void UseOutlook()\n{\n    Outlook.Application outlookApp = new Outlook.Application();\n    // Perform operations with outlookApp...\n\n    int result = Marshal.ReleaseComObject(outlookApp);\n    // Optionally, you can also set the reference to null for clarity.\n    outlookApp = null;\n    \n    // It's a good practice to force garbage collection\n    GC.Collect();\n    GC.WaitForPendingFinalizers();\n}