public DataSet GetData(int identifier)\n    {\n        DataSet ds = new DataSet();\n        dataAdapter.SelectCommand.CommandText = "SELECT * FROM table1 WHERE ident = @ident";\n        var param = dataAdapter.SelectCommand.CreateParameter();\n        param.ParameterName = "@ident";\n        param.Value = identifier;\n        dataAdapter.SelectCommand.Parameters.Add(param);\n\n        try\n        {\n            dataAdapter.Fill(ds);\n            if (ds.Tables[0].Rows.Count == 0)\n                throw new DataNotFoundException("Data not found for the provided identifier.");\n\n            return ds;\n        }\n        catch (Exception ex)\n        {\n            // Handle other possible exceptions here\n            throw;\n        }\n    }