using System;\nusing System.Data.SqlClient;\nusing System.Web.Caching;\n\npublic class SearchEngine\n{\n    private string connectionString = "your_connection_string_here";\n    \n    public DataTable PerformSearch(string query)\n    {\n        // Create a unique cache key based on the search query\n        string cacheKey = "search_results_" + query.ToLower();\n        \n        // Try to retrieve the data from the cache\n        DataTable cachedData = (DataTable)HttpContext.Current.Cache[cacheKey];\n        if (cachedData != null)\n            return cachedData;\n\n        // If not found in cache, execute the database query\n        DataTable results = ExecuteDatabaseQuery(query);\n        \n        // Store the retrieved data in the cache with a time-out of 10 minutes\n        HttpContext.Current.Cache.Insert(cacheKey, results, null,\n                                         DateTime.Now.AddMinutes(10), Cache.NoSlidingExpiration);\n\n        return results;\n    }\n\n    private DataTable ExecuteDatabaseQuery(string query)\n    {\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            SqlCommand command = new SqlCommand(query, connection);\n            SqlDataAdapter adapter = new SqlDataAdapter(command);\n            DataTable table = new DataTable();\n            adapter.Fill(table);\n            return table;\n        }\n    }\n}