using System;\nusing System.Net.Http;\nusing System.Threading.Tasks;\n\npublic class ImageChecker\n{\n    private static readonly HttpClient client = new HttpClient();\n\n    public async Task CheckImageExistsAsync(string url)\n    {\n        try\n        {\n            HttpResponseMessage response = await client.SendAsync(new HttpRequestMessage(HttpMethod.Head, url));\n            \n            if (response.IsSuccessStatusCode)\n            {\n                Console.WriteLine("The image is accessible.");\n            }\n            else\n            {\n                throw new Exception($"Failed to access the image. Status code: {response.StatusCode}");\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"An error occurred: {ex.Message}");\n        }\n    }\n}\n\n// Usage example:\nclass Program\n{\n    static async Task Main(string[] args)\n    {\n        ImageChecker checker = new ImageChecker();\n        await checker.CheckImageExistsAsync("http://yourserver.com/media/1/image.jpg");\n    }\n}