using System;\nusing System.Text.RegularExpressions;\n\nclass Program\n{\n    static void Main()\n    {\n        string input = "The big <bpt i=\"1\" x=\"1\" type=\"bold\"><b></bpt>black<ept i=\"1\"></b></ept> <bpt i=\"2\" x=\"2\" type=\"ulined\"><u></bpt>cat<ept i=\"2\"></u></ept> sleeps.";\n        \n        // Remove <bpt *>*</bpt>\n        string patternBpt = @"<bpt\b[^>]*>.*?</bpt>";\n        input = Regex.Replace(input, patternBpt, string.Empty, RegexOptions.Singleline);\n\n        // Remove <ept *>*</ept>\n        string patternEpt = @"<ept\b[^>]*>.*?</ept>";\n        input = Regex.Replace(input, patternEpt, string.Empty, RegexOptions.Singleline);\n        \n        Console.WriteLine(input);\n    }\n}