using System;\nusing System.Collections.Generic;\nusing FlickrNet;\n\nnamespace FlickrPhotoSlideshow\n{\n    class Program\n    {\n        static void Main(string[] args)\n        {\n            string apiKey = "YOUR_API_KEY";\n            string sharedSecret = "YOUR_SHARED_SECRET";\n            \n            // Authentication details (OAuth tokens should be securely managed)\n            OAuthToken token = new OAuthToken("USER_OAUTH_TOKEN", "USER_OAUTH_TOKEN_SECRET");\n\n            Flickr flickr = new Flickr(apiKey, sharedSecret);\n            flickr.AuthenticationToken = token;\n\n            var photosets = flickr.PhotosetsGetList();\n            \n            foreach (var photoSet in photosets)\n            {\n                // Get list of photos in the photoset\n                PhotoSearchOptions options = new PhotoSearchOptions\n                {\n                    PhotosetId = photoSet.PhotosetId,\n                    Page = 1, PerPage = 100 // Adjust per page and handle pagination if necessary\n                };\n                \n                var photos = flickr.PhotosSearch(options);\n                \n                foreach (Photo photo in photos)\n                {\n                    string photoUrl = flickr.PhotosGetSizes(photo.PhotoId)[0].Source; // Original size\n                    // Here you can generate the HTML for your slideshow with photoUrl\n                }\n            }\n        }\n    }\n}