using System.Xml;\nusing System.Xml.Schema;\n\npublic bool ValidateXml(XDocument xmlDoc, string xsdPath)\n{\n    var isValid = true;\n\n    XmlSchemaSet schemas = new XmlSchemaSet();\n    schemas.Add(null, xsdPath);\n\n    xmlDoc.Validate(schemas, (o, e) =>\n    {\n        Console.WriteLine($"XML Validation Error: {e.Message}");\n        isValid = false;\n    });\n\n    return isValid;\n}