using Gtk;\n\npublic class MyWindow : Window\n{\n    private VBox vbox; // Or any other container\n\n    public MyWindow() : base(WindowType.Toplevel)\n    {\n        Title = "My Custom Window";\n        vbox = new VBox();\n        Add(vbox);\n        // Add your widgets to the vbox here, e.g., vbox.PackStart(new Button("Click Me"), true, true, 0);\n        ShowAll();\n    }\n\n    public Widget GetContentWidget()\n    {\n        return vbox;\n    }\n}\n\n// Usage\npublic class MainWindow : Window\n{\n    private Notebook notebook;\n\n    public MainWindow() : base(WindowType.Toplevel)\n    {\n        Title = "Main Application";\n        SetDefaultSize(400, 300);\n\n        // Create a notebook and add it to the window\n        notebook = new Notebook();\n        Add(notebook);\n\n        // Create an instance of MyWindow or similar container-based widget\n        MyWindow myWindow = new MyWindow();\n\n        // Get the content widget (usually a VBox or similar) from MyWindow\n        Widget contentWidget = myWindow.GetContentWidget();\n\n        // Add the content widget to a new tab in the notebook\n        NotebookPage page = notebook.AppendPage(contentWidget, new Label("Tab Title"));\n    }\n\n    [STAThread]\n    public static void Main()\n    {\n        Application.Init();\n        MainWindow mainWindow = new MainWindow();\n        mainWindow.DeleteEvent += delegate { Application.Quit(); };\n        mainWindow.ShowAll();\n        Application.Run();\n    }\n}