using System;\nusing System.Data;\nusing MySql.Data.MySqlClient;\nusing Oracle.ManagedDataAccess.Client;\nusing IBM.Data.DB2.Core;\n\npublic class SqlServerAdapter : IDatabaseAdapter\n{\n    public string ConnectionString { get; set; }\n    public IDbConnection GetConnection() => new System.Data.SqlClient.SqlConnection(ConnectionString);\n    public IDbCommand CreateCommand(string commandText, CommandType commandType)\n    {\n        var command = new System.Data.SqlClient.SqlCommand(commandText);\n        command.CommandType = commandType;\n        return command;\n    }\n}\n\npublic class OracleAdapter : IDatabaseAdapter\n{\n    public string ConnectionString { get; set; }\n    public IDbConnection GetConnection() => new OracleConnection(ConnectionString);\n    public IDbCommand CreateCommand(string commandText, CommandType commandType)\n    {\n        var command = new OracleCommand(commandText.Replace("@", ":"));\n        command.CommandType = commandType;\n        return command;\n    }\n}\n\npublic class MySqlAdapter : IDatabaseAdapter\n{\n    public string ConnectionString { get; set; }\n    public IDbConnection GetConnection() => new MySqlConnection(ConnectionString);\n    public IDbCommand CreateCommand(string commandText, CommandType commandType)\n    {\n        var command = new MySqlCommand(commandText.Replace("@", "?"));\n        command.CommandType = commandType;\n        return command;\n    }\n}\n\npublic class Db2Adapter : IDatabaseAdapter\n{\n    public string ConnectionString { get; set; }\n    public IDbConnection GetConnection() => new Db2Connection(ConnectionString);\n    public IDbCommand CreateCommand(string commandText, CommandType commandType)\n    {\n        var command = new Db2Command(commandText.Replace("@", "?"));\n        command.CommandType = commandType;\n        return command;\n    }\n}