using System;\nusing Flurl.Http;\nusing HtmlAgilityPack;\n\nclass Program\n{\n    static async void Main()\n    {\n        // Send a GET request to the URL\n        var response = await "http://example.com".GetAsync();\n        \n        // Ensure the request was successful\n        response.EnsureSuccessStatusCode();\n\n        // Parse the HTML document\n        var htmlDoc = new HtmlDocument();\n        htmlDoc.LoadHtml(await response.GetStringAsync());\n\n        // You can now use htmlDoc to query and manipulate the HTML\n        var titleNode = htmlDoc.DocumentNode.SelectSingleNode("//title");\n        Console.WriteLine("Page Title: " + (titleNode?.InnerText ?? "No title found"));\n    }\n}