using System;\nusing System.IO;\nusing System.Xml.Linq;\n\npublic class XmlHandler\n{\n    private readonly string filePath;\n\n    public XmlHandler(string filePath)\n    {\n        this.filePath = filePath;\n    }\n\n    public void SaveXml(XDocument xmlDoc)\n    {\n        try\n        {\n            if (xmlDoc != null)\n            {\n                xmlDoc.Save(filePath);\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"Failed to save XML: {ex.Message}");\n        }\n    }\n\n    public XDocument LoadXml()\n    {\n        try\n        {\n            if (File.Exists(filePath))\n            {\n                var xmlDoc = XDocument.Load(filePath, LoadOptions.SetLineInfo);\n                return xmlDoc;\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"Failed to load XML: {ex.Message}");\n        }\n\n        return null;\n    }\n}