[ClientScriptResource("MyCustomControls.MyAjaxButtonBehavior", "MyCustomControls.Scripts.MyAjaxButton.js")]\npublic class MyAjaxButton : ScriptControl\n{\n    public string ButtonText { get; set; }\n\n    protected override void CreateChildControls()\n    {\n        var button = new Web.UI.WebControls.Button();\n        button.ID = "InnerButton";\n        button.Text = ButtonText;\n        Controls.Add(button);\n\n        // Register the script descriptor for this control\n        ScriptManager.RegisterScriptDescriptors(this);\n    }\n\n    [System.Web.Services.WebMethod()]\n    public static string MyAjaxMethod()\n    {\n        return "Hello from server!";\n    }\n}