using System;\nusing System.Data;\nusing System.Data.SqlClient;\n\nclass Program\n{\n    static void Main()\n    {\n        string connectionString = "your connection string here";\n        \n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            connection.Open();\n            \n            // Enable IDENTITY_INSERT for the orders table\n            SqlCommand cmdEnableIdentityInsert = new SqlCommand("SET IDENTITY_INSERT orders ON;", connection);\n            cmdEnableIdentityInsert.ExecuteNonQuery();\n\n            // Execute your dataset insert logic here\n            InsertData(connection); // Assume this method contains your insert logic using the same connection\n\n            // Disable IDENTITY_INSERT when done\n            SqlCommand cmdDisableIdentityInsert = new SqlCommand("SET IDENTITY_INSERT orders OFF;", connection);\n            cmdDisableIdentityInsert.ExecuteNonQuery();\n        }\n    }\n\n    static void InsertData(SqlConnection connection)\n    {\n        string query = "INSERT INTO orders (order_Id, column2) VALUES (@orderId, @value)";\n        \n        using (SqlCommand insertCommand = new SqlCommand(query, connection))\n        {\n            insertCommand.Parameters.AddWithValue("@orderId", 1);\n            insertCommand.Parameters.AddWithValue("@value", "SampleValue");\n            \n            insertCommand.ExecuteNonQuery();\n        }\n    }\n}