[TestCase("file1.pdf", 100, 5)]\n    [TestCase("file2.pdf", 200, 10)]\n    public void TestPDFExtraction(string filePath, int expectedWordCount, int expectedImageCount)\n    {\n        var pdfProcessor = new PDFProcessor();\n        var result = pdfProcessor.ExtractContent(filePath);\n        Assert.AreEqual(expectedWordCount, result.WordCount);\n        Assert.AreEqual(expectedImageCount, result.ImageCount);\n    }