using System;\nusing System.Web.Services;\nusing System.Text.RegularExpressions;\n\nnamespace YourNamespace\n{\n    public partial class EmailValidation : System.Web.UI.Page\n    {\n        [WebMethod]\n        public static bool CheckEmail(string emailId)\n        {\n            // Simple regex for basic validation\n            string pattern = @"^\w+([-+.']\w+)*@\w+([-.]\w+)*\.\w+([-.]\w+)*$";\n            \n            if (Regex.IsMatch(emailId, pattern))\n            {\n                // Here you can add logic to check against a database for uniqueness\n                // For this example, we assume all emails not matching regex are invalid.\n                return true;\n            }\n            else\n            {\n                return false;\n            }\n        }\n    }\n}