using System;\nusing System.Security.Cryptography;\nusing Microsoft.Win32;\n\nclass Program\n{\n    static void Main()\n    {\n        string applicationName = "MySecureApp";\n        string keyName = "EncryptionKey";\n\n        // Encrypt a sample encryption key using the current user's credentials\n        byte[] plainKey = { 1, 2, 3, 4, 5 }; // Your actual AES key, for example\n        byte[] encryptedKey = ProtectedData.Protect(plainKey, null, DataProtectionScope.CurrentUser);\n        \n        // Store the encrypted key in Windows Registry or file system securely\n        Registry.SetValue($"HKEY_CURRENT_USER\\Software\\{applicationName}", keyName, Convert.ToBase64String(encryptedKey));\n\n        // Retrieve and decrypt the key when needed\n        string encryptedKeyValue = (string)Registry.GetValue($"HKEY_CURRENT_USER\\Software\\{applicationName}", keyName, null);\n        byte[] retrievedEncryptedKey = Convert.FromBase64String(encryptedKeyValue);\n        byte[] decryptedKey = ProtectedData.Unprotect(retrievedEncryptedKey, null, DataProtectionScope.CurrentUser);\n\n        Console.WriteLine("Decrypted Key: " + BitConverter.ToString(decryptedKey));\n    }\n}