using Microsoft.VisualStudio.Modeling.Presentation;\nusing Microsoft.VisualStudio.Modeling.Shell;\n\nnamespace YourDslName\n{\n    public partial class YourDiagram : Diagram\n    {\n        protected override void OnDragDrop(DropEventArgs de)\n        {\n            if (de.Data.GetDataPresent(typeof(ITable)))\n            {\n                ITable table = de.Data.GetData(typeof(ITable)) as ITable;\n                \n                // Create a new domain class instance and add it to the diagram based on the table data\n                this.CurrentPropertyStore.TransactionManager.DoWithinTransaction("Import Table", delegate()\n                {\n                    var newElement = new YourDomainClass(this.Store);\n                    newElement.Name = table.Name; // Assuming 'Name' is a property of your custom element and table\n\n                    // Add any additional properties or relationships as needed\n                });\n\n                de.Handled = true;\n            }\n            base.OnDragDrop(de);\n        }\n\n        protected override void OnDragEnter(DragEventArgs de)\n        {\n            if (de.Data.GetDataPresent(typeof(ITable)))\n            {\n                de.Effect = DragDropEffects.Copy;\n                de.Handled = true;\n            }\n            base.OnDragEnter(de);\n        }\n    }\n}