using (MyDataContext db = new MyDataContext())\n{\n    // Create a new instance of the entity\n    var newEntity = new YourTable()\n    {\n        Column1 = "Value1",\n        Column2 = "Value2",\n        // Do not set the IDENTITY column value here!\n    };\n\n    // Insert the object into the table.\n    db.YourTables.InsertOnSubmit(newEntity);\n\n    // Submit all changes in this transaction.\n    db.SubmitChanges();\n\n    // After submitting changes, newEntity.ID will contain the generated ID\n    Console.WriteLine("Generated ID: " + newEntity.ID);\n}