// Configure NHibernate for SQLite in-memory database\nvar configuration = new Configuration();\nconfiguration.Configure(); // Assumes hibernate.cfg.xml is set up correctly\nconfiguration.SetProperty(Environment.ConnectionProvider, "NHibernate.Connection.DriverConnectionProvider");\nconfiguration.SetProperty(Environment.Dialect, "NHibernate.Dialect.SQLiteDialect");\nconfiguration.SetProperty(Environment.ConnectionString, "Data Source=:memory:;Version=3;New=True");\n\n// Build the session factory\nISessionFactory sessionFactory = configuration.BuildSessionFactory();\n\nusing (ISession session = sessionFactory.OpenSession())\nusing (var transaction = session.BeginTransaction())\n{\n    // Perform database operations here\n    var exampleEntity = new ExampleEntity { Property1 = "Value" };\n    session.Save(exampleEntity);\n    \n    // Commit the transaction\n    transaction.Commit();\n}\n\n// If you need to share schema across sessions, consider using a separate SchemaExport step before tests start