using System;\nusing ReportService2010;\n\nclass Program\n{\n    static void Main()\n    {\n        // Initialize the Reporting Services Web Service proxy.\n        ReportingService2010 rs = new ReportingService2010();\n        rs.Credentials = System.Net.CredentialCache.DefaultCredentials;\n        rs.Url = "http://<ServerName>/ReportServer/ReportService2010.asmx";\n\n        try\n        {\n            // Define the report path and other subscription parameters.\n            string report = "/Path/To/Your/Report";\n            string description = "Description of the subscription";\n            string eventType = "TimedSubscription"; // Type of event: Timed, Snapshot, or DataDriven\n            string matchData = "<ScheduleDefinition><StartDateTime>2023-10-01T09:00:00Z</StartDateTime></ScheduleDefinition>";\n            ExtensionSettings extSettings = new ExtensionSettings();\n            extSettings.Extension = "Report Server Email"; // Extension name\n\n            // Specify delivery settings.\n            ParameterValue[] values = new ParameterValue[1];\n            values[0] = new ParameterValue();\n            values[0].Name = "TO";\n            values[0].Value = "email@example.com";\n\n            extSettings.ParameterValues = values;\n\n            // Define data source credentials, if necessary.\n            DataSourceCredentials[] credentials = null;\n            \n            string status;\n            string subscriptionId = rs.CreateSubscription(eventType, matchData, report, description,\n                                                      extSettings, credentials, out status);\n            Console.WriteLine("Created Subscription with ID: " + subscriptionId);\n        }\n        catch (Exception e)\n        {\n            Console.WriteLine(e.Message);\n        }\n    }\n}