using System.Security;\nusing System.Security.Policy;\nusing System.Reflection;\n\npublic void ModifyCodeGroup()\n{\n    PolicyLevel userPolicy = SecurityManager.GetPolicyLevel("User");\n\n    UnionCodeGroup newGroup = new UnionCodeGroup(\n        new ZoneMembershipCondition(SecurityZone.MyComputer),\n        new PermissionSet(PermissionState.None)\n    );\n\n    // Add your specific permissions to the permission set\n    newGroup.PermissionSet.AddPermission(new FileIOPermission(FileIOPermissionAccess.NoAccess));\n\n    userPolicy.RootCodeGroup.AddChild(newGroup);\n\n    SecurityManager.SavePolicy();\n}