class Student\n{\n    public long Pk { get; set; }\n    public string Name { get; set; }\n    public List<Schedule> Schedules { get; set; } = new List<Schedule>();\n\n    // Method to add a schedule and maintain the back-reference\n    public void AddSchedule(Schedule schedule)\n    {\n        schedule.Student = this;\n        Schedules.Add(schedule);\n    }\n}\n\nclass Schedule\n{\n    public string Semester { get; set; }\n    public List<Course> Courses { get; set; } = new List<Course>();\n    public Student Student { get; private set; } // Back-reference to Student\n\n    // Constructor or method to initialize the back-reference if needed\n}