try {\n    dataSet.EnforceConstraints = true;\n} catch (System.Data.ConstraintException ex) {\n    foreach (DataTable table in dataSet.Tables) {\n        if(table.HasErrors) {\n            DataRow errorRow = table.GetErrors()[0];\n            Console.WriteLine($"Table '{table.TableName}' has errors:");\n            \n            foreach(DataColumn column in table.Columns) {\n                if (errorRow.GetColumnError(column) != "") {\n                    Console.WriteLine($"- Column: {column.ColumnName}, Error: {errorRow.GetColumnError(column)}");\n                }\n            }\n        }\n    }\n}