using OpenQA.Selenium;\nusing OpenQA.Selenium.Chrome;\n\nclass Program\n{\n    static void Main()\n    {\n        // Initialize ChromeDriver\n        var driver = new ChromeDriver();\n\n        // Navigate to a URL\n        driver.Navigate().GoToUrl("http://example.com");\n\n        // Go back\n        driver.Navigate().Back();\n\n        // Go forward\n        driver.Navigate().Forward();\n\n        // Open a new tab (you can also navigate to a specific URL if needed)\n        ((IJavaScriptExecutor)driver).ExecuteScript("window.open('');");\n\n        // Switch to the new tab\n        driver.SwitchTo().Window(driver.WindowHandles[1]);\n\n        // Close the current tab in focus\n        driver.Close();\n\n        // Open a new window\n        ((IJavaScriptExecutor)driver).ExecuteScript("window.open('', '_blank', 'left=50,top=20,width=300,height=400,personalbar=0,toolbar=0,scrollbars=1,resizable=0');");\n\n        driver.SwitchTo().Window(driver.WindowHandles[2]);\n\n        // Close the current window (Note: Closing all windows will close the browser)\n        driver.Close();\n\n        // Quit all open instances of ChromeDriver\n        driver.Quit();\n    }\n}