void Application_Start(object sender, EventArgs e)\n{\n    // Initialize the file locations list in application state when the app starts\n    Application.Lock();\n    Application["FileLocations"] = new List<string>();\n    Application.UnLock();\n}\n\nvoid Application_End(object sender, EventArgs e)\n{\n    // Code that runs on application shutdown\n    if (Application["FileLocations"] != null)\n    {\n        var fileLocations = (List<string>)Application["FileLocations"];\n        foreach (string file in fileLocations)\n        {\n            if (System.IO.File.Exists(file))\n            {\n                System.IO.File.Delete(file);\n            }\n        }\n    }\n}\n\npublic static void AddTempFilePath(string filePath)\n{\n    // Lock the application state before modifying it\n    HttpContext.Current.Application.Lock();\n    var fileLocations = (List<string>)HttpContext.Current.Application["FileLocations"];\n    fileLocations.Add(filePath);\n    HttpContext.Current.Application["FileLocations"] = fileLocations;\n    HttpContext.Current.Application.UnLock();\n}