public void DoSomething(DataRow row) {\n    var sqlStringStatus = (SqlString)row["StatusCodeColumn"];\n    StatusCode statusCode = sqlStringStatus.ToStatusCode();\n\n    if (statusCode == StatusCode.Failure) {\n        throw new InvalidOperationException("Operation failed due to failure status.");\n    }\n\n    switch (statusCode) {\n        case StatusCode.Unknown:\n            Console.WriteLine("Status is unknown, please verify.");\n            break;\n        // Handle other cases\n        default:\n            Console.WriteLine($"Processing {statusCode}...");\n            break;\n    }\n}