using System;\nusing System.IO;\nusing System.Text.RegularExpressions;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        string filePath = "C:\\path\\to\\tnsnames.ora"; // Specify the path to your tnsnames.ora file\n\n        if (File.Exists(filePath))\n        {\n            string content = File.ReadAllText(filePath);\n            ParseTnsNames(content);\n        }\n        else\n        {\n            Console.WriteLine("The specified file does not exist.");\n        }\n    }\n\n    static void ParseTnsNames(string content)\n    {\n        // Regular expression pattern to match TNS entries\n        string pattern = @"(\w+)\s*=\s*\(([\w\s\(\)\=]+?)\)";\n        MatchCollection matches = Regex.Matches(content, pattern);\n\n        foreach (Match match in matches)\n        {\n            Console.WriteLine("TNS Name: " + match.Groups[1].Value);\n            Console.WriteLine("Description:");\n            Console.WriteLine(match.Groups[2].Value.Trim());\n            Console.WriteLine();\n        }\n    }\n}