using System.Runtime.InteropServices;\nusing System.Threading;\n\npublic class MouseController\n{\n    [DllImport("user32.dll", CharSet = CharSet.Auto, CallingConvention = CallingConvention.StdCall)]\n    public static extern void mouse_event(uint dwFlags, uint dx, uint dy, uint cButtons, uint dwExtraInfo);\n    \n    private const int MOUSEEVENTF_LEFTDOWN = 0x02;\n    private const int MOUSEEVENTF_LEFTUP = 0x04;\n\n    public static void LeftMouseDown()\n    {\n        mouse_event(MOUSEEVENTF_LEFTDOWN, X: 0, Y: 0, cButtons: 0, dwExtraInfo: 0);\n    }\n\n    public static void LeftMouseUp()\n    {\n        mouse_event(MOUSEEVENTF_LEFTUP, X: 0, Y: 0, cButtons: 0, dwExtraInfo: 0);\n    }\n\n    public static void MoveMouse(int x, int y)\n    {\n        System.Windows.Forms.Cursor.Position = new System.Drawing.Point(x, y);\n    }\n}\n\n// Usage\nMouseController.MoveMouse(100, 150);\nMouseController.LeftMouseDown();\nThread.Sleep(100); // Hold down the button for a short period.\nMouseController.LeftMouseUp();