using System;\nusing System.DirectoryServices;\n\nclass Program\n{\n    static void Main()\n    {\n        // Use the credential cache for authentication\n        DirectoryEntry de = new DirectoryEntry("LDAP://DC=example,DC=com", null, null, AuthenticationTypes.Secure);\n\n        try\n        {\n            using (DirectorySearcher searcher = new DirectorySearcher(de))\n            {\n                searcher.Filter = "(&(objectClass=user)(sAMAccountName=username))"; // replace 'username' with the actual username\n\n                SearchResult result = searcher.FindOne();\n\n                if (result != null)\n                {\n                    Console.WriteLine("User Name: " + GetValue(result, "displayName"));\n                    Console.WriteLine("Telephone Number: " + GetValue(result, "telephoneNumber"));\n                }\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Error: " + ex.Message);\n        }\n    }\n\n    static string GetValue(SearchResult result, string propertyName)\n    {\n        if (result.Properties.Contains(propertyName))\n            return result.Properties[propertyName][0].ToString();\n        else\n            return "N/A";\n    }\n}