using System;\nusing System.DirectoryServices.AccountManagement;\n\nclass Program\n{\n    static void Main()\n    {\n        // Use the credential cache for authentication\n        using (PrincipalContext context = new PrincipalContext(ContextType.Domain))\n        {\n            UserPrincipal user = UserPrincipal.FindByIdentity(context, "username"); // replace 'username' with the actual username\n\n            if (user != null)\n            {\n                Console.WriteLine("User Name: " + user.DisplayName);\n                Console.WriteLine("Telephone Number: " + user.VoiceTelephoneNumber); // Note: VoiceTelephoneNumber is used for phone numbers\n            }\n        }\n    }\n}