public class PackageViewModelBuilder\n{\n    public PackageDetailsViewModel Build(Package package)\n    {\n        // Logic to build the view model from the package data\n        return new PackageDetailsViewModel \n        { \n            Id = package.Id,\n            Name = package.Name,\n            Status = package.Status\n        };\n    }\n}\n\npublic class PackagesController : Controller\n{\n    private readonly IPackageService _packageService;\n    private readonly PackageViewModelBuilder _viewModelBuilder;\n\n    public PackagesController(IPackageService packageService, PackageViewModelBuilder viewModelBuilder)\n    {\n        _packageService = packageService;\n        _viewModelBuilder = viewModelBuilder;\n    }\n\n    [HttpGet]\n    public ActionResult Details(int id)\n    {\n        var package = _packageService.GetPackageById(id);\n        var vm = _viewModelBuilder.Build(package);\n\n        return View(vm);\n    }\n}