public abstract class FooBase\n{\n    public event EventHandler<string> StatusTextChanged;\n\n    private string _statusText = "Idle.";\n\n    public string StatusText\n    {\n        get => _statusText;\n        protected set\n        {\n            if (_statusText == value) return; // No need to raise an event if the text did not change.\n\n            _statusText = value;\n            RaiseEventIfSubscribed(StatusTextChanged, value);\n        }\n    }\n\n    private void RaiseEventIfSubscribed(EventHandler<string> eventToRaise, string arg)\n    {\n        var handler = eventToRaise;\n        handler?.Invoke(this, arg); // Safe invocation for thread safety and null check.\n    }\n}