using System;\nusing System.Security.Cryptography.Pkcs;\nusing System.Security.Cryptography.X509Certificates;\n\npublic void ValidateCodeSignature(string assemblyPath)\n{\n    X509Certificate2 certificate = null;\n    SignedCms signedCms = new SignedCms();\n    ContentInfo contentInfo = new ContentInfo(File.ReadAllBytes(assemblyPath));\n    \n    signedCms.Decode(contentInfo);\n    signedCms.CheckSignature(true);\n\n    foreach (SignerInfo signer in signedCms.SignerInfos)\n    {\n        certificate = signer.Certificate;\n        // Verify the thumbprint or other properties of the certificate\n        if (!certificate.Thumbprint.Equals("your-known-thumbprint"))\n            throw new UnauthorizedAccessException("Assembly is not from a trusted source.");\n    }\n}