using System;\nusing System.IO;\nusing System.Windows.Forms;\n\npublic partial class MainForm : Form\n{\n    public MainForm()\n    {\n        InitializeComponent();\n    }\n\n    private void btnSaveCsv_Click(object sender, EventArgs e)\n    {\n        // Create an instance of the SaveFileDialog class.\n        using (SaveFileDialog saveFileDialog = new SaveFileDialog())\n        {\n            // Set the default file name and extension.\n            saveFileDialog.FileName = "untitled.csv";\n            saveFileDialog.DefaultExt = ".csv";\n\n            // Set filter options and filter index.\n            saveFileDialog.Filter = "CSV Files (*.csv)|*.csv|All Files (*.*)|*.*";\n            saveFileDialog.FilterIndex = 1;\n\n            // Display the SaveFileDialog.\n            if (saveFileDialog.ShowDialog() == DialogResult.OK)\n            {\n                string contentToSave = "Your,Csv,Data,Here"; // This is the data you want to save.\n\n                // Write the string data to the file specified by the user.\n                File.WriteAllText(saveFileDialog.FileName, contentToSave);\n            }\n        }\n    }\n}