using System;\nusing System.IO;\nusing Microsoft.SharePoint;\n\npublic class DocumentUploader {\n    public void UploadToSharePoint(string siteUrl, string libraryName, string filePath, string documentName) {\n        using (SPSite site = new SPSite(siteUrl)) {\n            using (SPWeb web = site.OpenWeb()) {\n                SPFolder folder = web.Folders[libraryName];\n                byte[] fileContents = System.IO.File.ReadAllBytes(filePath);\n                \n                folder.Files.Add(documentName, fileContents, true); // true to overwrite if exists\n            }\n        }\n    }\n}