using System;\nusing System.DirectoryServices.AccountManagement;\n\nstring domainAndUsername = HttpContext.Current.User.Identity.Name; // e.g., "DOMAIN\\username"\nint backslashIndex = domainAndUsername.IndexOf('\\');\nif (backslashIndex != -1)\n{\n    string username = domainAndUsername.Substring(backslashIndex + 1);\n    using (PrincipalContext pc = new PrincipalContext(ContextType.Domain))\n    {\n        UserPrincipal user = UserPrincipal.FindByIdentity(pc, username);\n        if (user != null)\n        {\n            string displayName = user.DisplayName;\n            // Use the display name as needed\n        }\n    }\n}