using System;\nusing System.Text.RegularExpressions;\n\npublic class HtmlEncoderHelper\n{\n    public static string EncodeWordContent(string input)\n    {\n        if (string.IsNullOrEmpty(input))\n            return input;\n\n        // Replace smart quotes with standard quotes or appropriate HTML entities\n        input = input.Replace("“", "&ldquo;"); // Left double quotation mark\n        input = input.Replace("”", "&rdquo;"); // Right double quotation mark\n        input = input.Replace("‘", "&lsquo;"); // Left single quotation mark\n        input = input.Replace("’", "&rsquo;"); // Right single quotation mark\n\n        // Replace other common Word characters\n        input = input.Replace("–", "&ndash;"); // En dash\n        input = input.Replace("—", "&mdash;"); // Em dash\n        input = input.Replace("…", "&hellip;"); // Ellipsis\n        input = input.Replace("⁠", ""); // Word Joiner, often invisible but problematic\n\n        // Use Regex to remove or replace any other non-ASCII characters if needed\n        input = Regex.Replace(input, @"[^\u0000-\u007F]", m => $"&#{(int)m.Value[0]};");\n\n        return System.Web.HttpUtility.HtmlEncode(input);\n    }\n}