using (var session = sessionFactory.OpenSession())\n{\n    using (var transaction = session.BeginTransaction())\n    {\n        string hql = "DELETE FROM Accounts WHERE amount < :amountThreshold";\n        int rowCount = session.CreateQuery(hql)\n            .SetParameter("amountThreshold", 1000)\n            .ExecuteUpdate();\n\n        Console.WriteLine($"{rowCount} rows were deleted.");\n\n        transaction.Commit();\n    }\n}