using System.Data.SQLite;\n\nstring connectionString = "Data Source=path\\to\\your\\database.sqlite;Version=3;";\nusing (var con = new SQLiteConnection(connectionString))\n{\n    con.Open();\n    \n    // Insert or update\n    using (var cmd = new SQLiteCommand(con))\n    {\n        cmd.CommandText = @"INSERT INTO object_states (id, state) VALUES (@Id, @State)\n                             ON CONFLICT(id) DO UPDATE SET state = excluded.state;";\n        cmd.Parameters.AddWithValue("@Id", 1);\n        cmd.Parameters.AddWithValue("@State", "Active");\n        cmd.ExecuteNonQuery();\n    }\n\n    // Read\n    using (var cmd = new SQLiteCommand(con))\n    {\n        cmd.CommandText = @"SELECT state FROM object_states WHERE id=@Id;";\n        cmd.Parameters.AddWithValue("@Id", 1);\n        string state = (string)cmd.ExecuteScalar();\n        Console.WriteLine(state);\n    }\n}